<?php/** * Hello World widget class * class name must start in upper case and end with "Widget" */class HelloworldWidget {	/**	 * Helloworld example widget	 * [FIELD_NAME] => [	 * 		'label' => [FIELD_LABEL],	 * 		'type' => [FIELD_TYPE], // such as below	 * 		'items' => [ARRAY_OF_VALUES] // for specific types (combo, checkbox, radio, optgroup, list)	 * 		[ANY_HTML_ATTRIBUTE] => [HTML_ATTRIBUTE_VALUE]	 * ]	 * @var array	 */	public $helloworld_foobar_widget = [		'title_field' => [			'label' => 'Title',			'type' => 'text',			'placeholder' => 'Enter title...',			'autocomplete' => 'off'		],		'combo_field' => [			'label' => 'Combo',			'type' => 'combo',			'items' => ['1' => 'foo', '2' => 'bar']		],		'textarea_field' => [			'label' => 'Textarea',			'type' => 'textarea',			'placeholder' => 'textarea placeholder',		],		'checkbox_field' => [			'label' => 'Checkboxes',			'type' => 'checkbox',			'items' => ['1' => 'foo', '2' => 'bar']		],		'radio_field' => [			'label' => 'Radio',			'type' => 'radio',			'items' => ['1' => 'foo', '2' => 'bar']		],		'color_field' => [			'label' => 'Color',			'type' => 'color'		],		'number_field' => [			'label' => 'Number',			'type' => 'number',			'placeholder' => '999'		],		'optgroup_field' => [			'label' => 'Option group',			'type' => 'optgroup',			'items' => [				'foobar' => ['1' => 'foo', '2' => 'bar'],				'raboof' => ['3' => 'rab', '4' => 'oof']			]		],		'list_field' => [			'label' => 'List',			'type' => 'list',			'items' => ['1' => 'foo', '2' => 'bar']		],	];		/**	 * widget method	 * with the same name as above property	 * @param  array $options from widgets table for this widget	 */	public function helloworld_foobar_widget($options) {		// widget title from first field		$this->title = $options['title_field'];		// view variables		$color = isset($options['color_field']) ? $options['color_field'] : null;		$combo = isset($options['combo_field']) ? $options['combo_field'] : null;		$radio = isset($options['radio_field']) ? $options['radio_field'] : null;		// load view file (/apps/helloworld/views/foobar_widget.tpl)		$this->content = load_view('helloworld', 'foobar_widget.tpl', ['color' => $color, 'combo' => $combo, 'radio' => $radio]);		// cache lifetime for this widget		// widget will be read from cache in further loads		// reload widget after 10 seconds		$this->lifetime = 10;	}}?>